package chat

import (
	"reflect"
	"testing"
)

func TestPromptToImageMessage_NoImage(t *testing.T) {
	in := "hello world"
	msgs, _ := PromptToImageMessage(in)
	if len(msgs) != 1 {
		t.Fatalf("want 1 msg, got %d", len(msgs))
	}
	m := msgs[0]
	if m.Role != "user" {
		t.Fatalf("role = %q, want user", m.Role)
	}
	if m.Content != in {
		t.Fatalf("content = %q, want %q", m.Content, in)
	}
	if len(m.ContentParts) != 0 {
		t.Fatalf("expected no content parts, got %v", m.ContentParts)
	}
}

func TestExtractB64Strings_NoB64(t *testing.T) {
	in := "just a normal prompt without images"
	b64s, parsed := extractB64Strings(in)
	if len(b64s) != 0 {
		t.Fatalf("expected no b64 strings, got %v", b64s)
	}
	if parsed != in {
		// Allow implementations that return empty parsed when nothing found,
		// but prefer exact passthrough.
		t.Fatalf("expected parsed to be passthrough or empty, got %q", parsed)
	}
}

func TestExtractB64Strings_Single(t *testing.T) {
	// Valid base64 payload ("hello world")
	img := "aGVsbG8gd29ybGQ="
	in := "please see this image " + img + " it's good"
	gotB64s, gotParsed := extractB64Strings(in)
	wantB64s := []string{img}
	wantParsed := "please see this image <IMG_0> it's good"
	if !reflect.DeepEqual(gotB64s, wantB64s) {
		t.Fatalf("b64 mismatch:\nwant: %v\n got: %v", wantB64s, gotB64s)
	}
	if gotParsed != wantParsed {
		t.Fatalf("parsed mismatch:\nwant: %q\n got: %q", wantParsed, gotParsed)
	}
}

func TestExtractB64Strings_MultipleAndPunctuation(t *testing.T) {
	img0 := "QUJDRA==" // "ABCD"
	img1 := "SGVsbG8=" // "Hello"
	in := "first " + img0 + " middle (" + img1 + "). end"
	gotB64s, gotParsed := extractB64Strings(in)
	wantB64s := []string{img0, img1}
	wantParsed := "first <IMG_0> middle (<IMG_1>). end"
	if !reflect.DeepEqual(gotB64s, wantB64s) {
		t.Fatalf("b64 mismatch:\nwant: %v\n got: %v", wantB64s, gotB64s)
	}
	if gotParsed != wantParsed {
		t.Fatalf("parsed mismatch:\nwant: %q\n got: %q", wantParsed, gotParsed)
	}
}

